data_configuration.sql_statement:
select *,'Order:'+[order no]+','+[subject] as [Production Order Display]   from (
	select  d.process_matrix_id, d.label, d.value,p.subject from bpm_process_detail d
inner join bpm_process_matrix m on d.process_matrix_id=m.id 
inner join bpm_process p on m.process_id=p.id and m.process_revn=p.revn and p.status='close'
inner join bpm_process_template t on t.id=p.process_template_id and t.name like 'DHR Production Confirmation%' 
) detailResult 
PIVOT (
	MAX(value) 
	for label in (
		[order No]
	)
)
as product_confirmation_order

(function(scanString){
if (!scanString) return;
let equipmentData= scanString.split(";");
let name=equipmentData[0].split(":")[1];
let id = equipmentData[1].split(":")[1];
let ca_due_date=new Date(equipmentData[2].split(":")[1]);
let ma_due_date=new Date(equipmentData[3].split(":")[1]);

let result={
    "Equipment Name":name,
    "Equipment ID":id,
    "Calibration Due Date":ca_due_date,
	"Maintenance Due Date":ma_due_date
};
return  result;
})('$$scan')

(function(batchNo,material,shift){
if (!batchNo||!material||!shift) return;
else return  `${batchNo}_${material}_${shift}`;
})('$$batchNo','$$material','$$shift')
