(function(scanString){
if (!scanString) return;
let equipmentData= scanString.split(";");
let name=equipmentData[0].split(":")[1];
let id = equipmentData[1].split(":")[1];
let ca_due_date=new Date(equipmentData[2].split(":")[1]);
let ma_due_date=new Date(equipmentData[3].split(":")[1]);
let check_item=equipmentData[4].split(":")[1].split("|");
let result=[];
for (let i=0;i<check_item.length;i++){
    result.push({
    "Equipment Name":name,
    "Equipment ID":id,
    "Calibration Due Date":ca_due_date,
	"Maintenance Due Date":ma_due_date,
    "Parameter Description":check_item[i]})
}
return  result;
})('$$scan')

(function(mode){
if (mode=='Create new') 
	return[{"choose production order":"__UNCHANGE","Input Existing PID":"__DELETE","Shift":"__DELETE","is_amend_existing":"__DELETE"}];
else if (mode=='Amend existing') 
	return[{"choose production order":"__DELETE","Input Existing PID":"__UNCHANGE","Shift":"__UNCHANGE","is_amend_existing":"yes"}];
else 
	return [{"choose production order":"__UNCHANGE","Input Existing PID":"__UNCHANGE","Shift":"__UNCHANGE","is_amend_existing":"__UNCHANGE"}];
})('$$mode')

(function(mode,choose_station,change_station){
//console.log('copy station! mode, choose_station,change_station:',mode+";"+choose_station+";"+change_station);
if (mode=='Create new')
	return choose_station;
else if (mode=='Amend existing') 
	return change_station;
})('$$mode','$$choose_station','$$change_station')

(function(scanString,mode){
if (!scanString||scanString==='NA') return;
if (mode==='Amend existing') return;
let chemicalData= scanString.split(";");
let result=[];
for (let i=0;i<chemicalData.length;i++){
result.push({"Chemical":chemicalData[i]})
}
return  result;
})('$$chemicalCheckResult','$$mode')

(function(value){
if (!value||value==='n/a') return;
else return  `Warning: ${value} have opened processes!`;
})('$$initiator_with_open_process')

(function(batchNo,stationNo,shift){
if (!batchNo||!stationNo||!shift) return;
else return  `${batchNo}_${stationNo}_${shift}`;
})('$$batchNo','$$stationNo','$$shift')

(function(work_together_with,line_approver){
if (!work_together_with||work_together_with==='n/a'||!line_approver||line_approver==='n/a') return "No";
if (work_together_with.indexOf(line_approver)>0) return "__DELETE";
else return "No"
})('$$work_together_with','$$line_approver')

