select *,'Order:'+[order no]+','+[subject] as [Production Order Display]   from (
	select  d.process_matrix_id, d.label, d.value,p.subject from bpm_process_detail d
inner join bpm_process_matrix m on d.process_matrix_id=m.id 
inner join bpm_process p on m.process_id=p.id and m.process_revn=p.revn and p.status='close'
inner join bpm_process_template t on t.id=p.process_template_id and t.name like '$$qc_cfm_family_tmpl'+'%' 
) detailResult 
PIVOT (
	MAX(value) 
	for label in (
		[order No]
	)
)
as qc_confirmation_order

(function(inputText){
const regex = /Order:(\d+)/;
const match = inputText.match(regex);
if (match) {
    return match[1];
} else {
    return;
}
})('$$order_display')

(function(prd_by,prd_on,qa_by,qa_on){
	let prdDate=new Date(prd_on);
	let formattedPrdDate = prdDate.toLocaleDateString('en-GB', {day: '2-digit', month: 'short', year: 'numeric'}).replace(/ /g, '-');
	let qaDate=new Date(qa_on);
	let formattedQaDate = qaDate.toLocaleDateString('en-GB', {day: '2-digit', month: 'short', year: 'numeric'}).replace(/ /g, '-');
	return {"prod_completed_by":`Name:${prd_by}, Date:${formattedPrdDate}   `,"qa_completed_by":`Name:${qa_by}, Date:${formattedQaDate}`}
})("$$prd_by","$$prd_on","$$qa_by","$$qa_on")
